import * as React from "react";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import CardActions from "@mui/material/CardActions";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ShareIcon from "@mui/icons-material/Share";
import Collapsible from "react-collapsible-paragraph";
import { useState, useEffect } from "react";

const Post = ({data}) => {

  const [liked, setLikeStatus] = useState(false);

  console.log(liked)

  const renderLike = () => {

    if (liked === false) {
      localStorage.setItem('likeStatus', false)
      return <FavoriteIcon color="disabled" />
    } else {
      localStorage.setItem('likeStatus', true)
      return <FavoriteIcon color="error" />
    }

  }

  return (
    <Card sx={{ maxWidth: 500 }}>
      <CardHeader title={data.title} subheader={data.date} />
      <CardMedia
        component="img"
        height="500"
        image={data.url}
        alt="Paella dish"
      />
      <CardContent>
        <Typography variant="body2" color="text.secondary">
          <Collapsible lines={2}>{data.explanation}</Collapsible>
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites" onClick={() => setLikeStatus(!liked)}>
          { renderLike() }
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
      </CardActions>
    </Card>
  );
}

export default Post
