import { Container } from "@mui/material";
import React, { useState } from "react";
import "./App.css";
import Axios from "axios";
import Post from "./components/Post";

function App() {

  const [APOD, setAPOD] = useState("");

  const getAPOD = () => {
    Axios.get(
      "https://api.nasa.gov/planetary/apod?api_key=ge9zXukzme64aq3NwaMD4fVSNsNfu1R7cvsWVFq7&start_date=2021-06-22&end_date=2021-07-22"
    ).then((response) => {
      setAPOD(response.data.title)
    });
  };

  return (
    <div className="App">
      <Container maxWidth="sm">
        <Post></Post>
      </Container>
      <button onClick={getAPOD}>Get APOD Right Now</button>
      console.log({APOD})
      {APOD}
    </div>
  );
}

export default App;
