import * as React from "react";
import Container from "@mui/material/Container";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import DatePicker from "react-datepicker";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";

export default function ButtonAppBar({
  setAPODStartDate,
  setAPODEndDate,
  setAPOD,
  startDate,
  endDate,
}) {
  const SelectDates = () => {

    const [newStartDate, setNewStartDate] = useState(startDate);
    const [newEndDate, setNewEndDate] = useState(endDate);

    const selectNewStartDate = (date) => {

      setAPOD([]);
    };

    const selectNewEndDate = (date) => {
      setAPODEndDate(date);
      setAPOD([]);
    };

    const setNewDates = () => {
      if (newStartDate < newEndDate) {
        setAPODStartDate(newStartDate);
        setAPODEndDate(newEndDate);
      } else {
        console.log("INVALID DATES")
      }
    }

    return (
      <div style={{ float: "left" }}>
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "25ch" },
          }}
          noValidate
          autoComplete="off"
        >
          <TextField
            id="outlined-basic"
            size="small"
            type="date"
            variant="outlined"
            onChange={(e) => setNewStartDate(e.target.value)}
          />
          <TextField
            id="outlined-basic"
            size="small"
            type="date"
            variant="outlined"
          />
          <Button
            variant="contained"
            onClick={(date) => selectNewStartDate(date)}
          >
            Text
          </Button>
        </Box>

        <div style={{ float: "left" }}>Start Date:</div>
        <div style={{ float: "left" }}>
          <DatePicker
            wrapperClassName="picker"
            selected={startDate}
            onChange={(date) => selectNewStartDate(date)}
          />
        </div>
        <div style={{ float: "left" }}>End Date: </div>
        <div style={{ float: "left" }}>
          <DatePicker
            wrapperClassName="datePicker"
            selected={endDate}
            onChange={(date) => selectNewEndDate(date)}
          />
        </div>
        <div></div>
      </div>
    );
  };

  return (
    <AppBar position="static">
      <Container maxWidth="xl">
        <Toolbar disableGutters>
          <Typography
            variant="h6"
            noWrap
            component="div"
            sx={{ mr: 2, display: { xs: "none", md: "flex" } }}
          >
            Spacestagram
          </Typography>

          <Box
            style={{ marginLeft: "auto" }}
            sx={{ justifyContent: "flex-end" }}
          >
            {SelectDates()}
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}
