import Post from "./Post";
import React, { useState, useEffect } from "react";
import Axios from "axios";
import { useParams } from "react-router-dom";

export default function SharablePostPage() {
  // get the username from route params
  const { postDate } = useParams();
  const [APOD, setAPOD] = useState([]);

  let apodCall =
    "https://api.nasa.gov/planetary/apod?api_key=ge9zXukzme64aq3NwaMD4fVSNsNfu1R7cvsWVFq7&start_date=" + postDate;

  useEffect(() => {
    Axios.get(apodCall).then((response) => {
      console.log(response.data);
      setAPOD(response.data);
    });
  }, [apodCall]);

  // now we have access to the username that came from the URL
  return <div>hi there {postDate}</div>;
}
