import { Container, CircularProgress } from "@mui/material";
import React, { useState, useEffect } from "react";
import { Routes, Route, Link } from "react-router-dom";
import "./App.css";
import Axios from "axios";
import Post from "./components/Post";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function App() {
  let defaultStartDate = new Date();
  let defaultEndDate = new Date();
  defaultStartDate.setDate(defaultStartDate.getDate() - 5);

  const [APOD, setAPOD] = useState([]);
  const [startDate, setAPODStartDate] = useState(defaultStartDate);
  const [endDate, setAPODEndDate] = useState(defaultEndDate);

  let apodCall =
    "https://api.nasa.gov/planetary/apod?api_key=ge9zXukzme64aq3NwaMD4fVSNsNfu1R7cvsWVFq7&start_date=" +
    startDate.getFullYear() +
    "-" +
    (startDate.getMonth() + 1) +
    "-" +
    startDate.getDate() +
    "&end_date=" +
    endDate.getFullYear() +
    "-" +
    (endDate.getMonth() + 1) +
    "-" +
    endDate.getDate();

  console.log(apodCall);
  console.log(startDate);
  console.log(endDate);

  useEffect(() => {
    Axios.get(apodCall).then((response) => {
      console.log(response.data);
      setAPOD(response.data);
    });
  }, [apodCall]);

  const renderPosts = () => {
    if (APOD.length !== 0) {
      return APOD.map((data) => <Post data={data}></Post>);
    } else {
      return <CircularProgress />;
    }
  };

  const SelectStartDate = () => {
    const selectNewStartDate = (date) => {
      setAPODStartDate(date);
      setAPOD([]);
    };

    const selectNewEndDate = (date) => {
      setAPODEndDate(date);
      setAPOD([]);
    };

    return (
      <div>
        <DatePicker
          selected={startDate}
          onChange={(date) => selectNewStartDate(date)}
        />
        <DatePicker
          selected={endDate}
          onChange={(date) => selectNewEndDate(date)}
        />
      </div>
    );
  };

  const Home = () => {
    return (
      <div className="App">
        {SelectStartDate()}
        <Container maxWidth="sm">{renderPosts()}</Container>
      </div>
    );
  }

  function About() {
    return (
      <>
        <main>
          <h2>Who are we?</h2>
          <p>That feels like an existential question, don't you think?</p>
        </main>
        <nav>
          <Link to="/">Home</Link>
        </nav>
      </>
    );
  }

  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="about" element={<About />} />
    </Routes>
  );
}

export default App;
