import { Container, Button, CircularProgress } from "@mui/material";
import React, { useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";
import Post from "./components/Post";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function App() {
  const [APOD, setAPOD] = useState([]);
  const [startDate, setAPODStartDate] = useState(new Date());
  const [endDate, setAPODEndDate] = useState(new Date());

  useEffect(() => {
    Axios.get(
      "https://api.nasa.gov/planetary/apod?api_key=ge9zXukzme64aq3NwaMD4fVSNsNfu1R7cvsWVFq7&start_date=" + startDate + "&end_date=" + endDate
    ).then((response) => {
      console.log(response.data);
      setAPOD(response.data);
    });
  }, []);

  const renderPosts = () => {
    if (APOD.length !== 0) {
      return APOD.map((data) => <Post data={data}></Post>);
    } else {
      return <CircularProgress />;
    }
  };

  const SelectStartDate = () => {
    return (
      <div>
        <DatePicker
          selected={startDate}
          onChange={(date) => setAPODStartDate(date)}
        />
        <DatePicker
          selected={endDate}
          onChange={(date) => setAPODEndDate(date)}
        />
        <Button variant="contained">Select Date</Button>
      </div>
    );
  };

  return (
    <div className="App">
      {SelectStartDate()}
      <Container maxWidth="sm">{renderPosts()}</Container>
    </div>
  );
}

export default App;
