import * as React from "react";
import { styled } from "@mui/material/styles";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import CardActions from "@mui/material/CardActions";
import Collapse from "@mui/material/Collapse";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ShareIcon from "@mui/icons-material/Share";
import ReactTextCollapse from '..'

const TEXT_COLLAPSE_OPTIONS = {
  collapse: false,
  collapseText: "... show more",
  expandText: "show less",
  minHeight: 100,
  maxHeight: 200,
  textStyle: {
    color: "blue",
    fontSize: "20px",
  },
};

export default function RecipeReviewCard() {
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardHeader title="Quadrantids of the North" subheader="2022-01-08" />
      <CardMedia
        component="img"
        height="194"
        image="https://apod.nasa.gov/apod/image/2201/QuadrantidsnorthernskyRadioTelescopeArray1024.jpg"
        alt="Paella dish"
      />
      <CardContent>
        <Typography variant="body2" color="text.secondary">
          <ReactTextCollapse options={TEXT_COLLAPSE_OPTIONS}>
            <p>
              Named for a forgotten constellation, the Quadrantid Meteor Shower
              puts on an annual show for planet Earth's northern hemisphere
              skygazers. The shower's radiant on the sky lies within the old,
              astronomically obsolete constellation Quadrans Muralis. That
              location is not far from the Big Dipper, at the boundaries of the
              modern constellations Bootes and Draco. In fact north star Polaris
              is just below center in this frame and the Big Dipper asterism
              (known to some as the Plough) is above it, with the meteor shower
              radiant to the right. Pointing back toward the radiant, Quadrantid
              meteors streak through the night in the panoramic skyscape, a
              composite of images taken in the hours around the shower's peak on
              January 4, 2022. Arrayed in the foreground are radio telescopes of
              the Chinese Spectral Radioheliograph, Mingantu Observing Station,
              Inner Mongolia, China. A likely source of the dust stream that
              produces Quadrantid meteors was identified in 2003 as an asteroid.
              Status Updates: Deploying the James Webb Space Telescope
            </p>
          </ReactTextCollapse>
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>
            Heat 1/2 cup of the broth in a pot until simmering, add saffron and
            set aside for 10 minutes.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
  );
}
