import { Container, CircularProgress } from "@mui/material";
import React, { useState, useEffect } from "react";
import "./App.css";
import Axios from "axios";
import Post from "./components/Post";import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function App() {
  const [APOD, setAPOD] = useState([]);

  useEffect(() => {
    Axios.get(
      "https://api.nasa.gov/planetary/apod?api_key=ge9zXukzme64aq3NwaMD4fVSNsNfu1R7cvsWVFq7&start_date=2020-04-11&end_date=2020-04-25"
    ).then((response) => {
      console.log(response.data);
      setAPOD(response.data);
    });
  }, []);

  function renderPosts() {
    if (APOD.length !== 0) {
      return APOD.map((data) => <Post data={data}></Post>);
    } else {
      return <CircularProgress />;
    }
  }

  const Example = () => {
    const [startDate, setStartDate] = useState(new Date());
    return (
      <DatePicker selected={startDate} onChange={(date) => setStartDate(date)} />
    );
  };

  return (
    <div className="App">
      <Container maxWidth="sm">{renderPosts()}</Container>
      { Example() }
    </div>
  );
}

export default App;
